// DEFAULT to matching HEIGHT on the icons
// if WIDTH ever needs to match, use WIDTH instead

// withShadow is not guaranteed due to border radius being unknown (defaults to 10px)
// control shadow from className or parent
// TODO: control shadow from svg
export const PlayStoreButton = ({
  campaign = null,
  className = '',
  height = undefined,
  newWindow = false,
  to = null,
  width = 115,
  withShadow = false,
  clickEventLocation = null,
  clickEventSection = null,
}) => (
  <OutboundLink
    className={`${styles.appLink} ${
      withShadow ? styles.googlePlayWithShadow : ''
    } ${className}`}
    newWindow={newWindow}
    to={to || playStoreLink({ campaign })}
    onClick={() => {
      clickEvent(clickEventLocation, clickEventSection, 'Play Store');
    }}
  >
    <GooglePlayBadge height={height} width={height ? undefined : width} />
  </OutboundLink>
);

export const AppStoreButton = ({
  campaign = null,
  className = '',
  height = undefined,
  newWindow = false,
  to = null,
  width = 115,
  withShadow = false,
  white = false,
  clickEventLocation = null,
  clickEventSection = null,
}) => (
  <OutboundLink
    className={`${styles.appLink} ${
      withShadow ? styles.appStoreWithShadow : ''
    } ${className}`}
    newWindow={newWindow}
    to={to || appStoreLink(campaign)}
    onClick={() => {
      clickEvent(clickEventLocation, clickEventSection, 'App Store');
    }}
  >
    {white ? (
      <AppleAppStoreBadgeWhite height={height} width={width} />
    ) : (
      <AppleAppStoreBadge height={height} width={height ? undefined : width} />
    )}
  </OutboundLink>
);

// clickEventLocation is a broad, top-level place where the button live: Homepage, Footer, Navigation, About
// clickEventSection is a specific section within the location, this is optional: Hero, Mobile Menu (of a nav)
function clickEvent(
  clickEventLocation,
  clickEventSection,
  appPlatformStoreName
) {
  const eventLabel = clickEventSection
    ? `${clickEventSection} ${appPlatformStoreName} Button`
    : `${appPlatformStoreName} Button`;
  trackEvent(
    null,
    {
      eventCategory: clickEventLocation,
      eventAction: 'Click',
      eventLabel,
    },
    // eslint-disable-next-line
    { providers: [ga] }
  );
}
